mapper.select:
select

from sysuser
where  username = #{username} and userpass=#{password}

mapper.insert:
insert into sysuser (id, username, userpass, 
  createdate, iskeyuser, keycode
  )
values (#{id,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{userpass,jdbcType=VARCHAR}, 
  #{createdate,jdbcType=TIMESTAMP}, #{iskeyuser,jdbcType=INTEGER}, #{keycode,jdbcType=VARCHAR}
  )
